import SideNavBar from "../Components/SideNavBar";
import NavBar from "../Components/NavBar";
import Box from "@mui/material/Box";
import axios from "axios";
//import obj from "../data/Data";
import { useReducer, useState, useEffect} from "react";
import AddVideo from "./AddVideo";
import BookList from "./BookList";
//import { useNavigate } from "react-router-dom";
// import Form from "./Form";

function Books() {
  const [editableBook, setEditableBook] = useState(null);
  //const [Boook, dispatch] = useReducer(bookReducer, obj);
  const [Boook, dispatch] = useReducer(bookReducer, []);

  useEffect(() => {
    // Fetch data from the API
    axios.get("http://localhost:8000/book")
      .then(response => {
        dispatch({ type: "SET_BOOKS", payload: response.data });
      })
      .catch(error => {
        console.error("Error fetching data:", error);
      });
  }, []);

  function bookReducer(Boook, action) {
    switch (action.type) {
      case "SET_BOOKS":
        return action.payload;
      case "ADD":
        return [...Boook, { ...action.payload, id: Boook.length + 1 }];
      case "DELETE":
        return Boook.filter((book) => book.id !== action.payload);
      case "EDIT":
        const index = Boook.findIndex((b) => b.id === action.payload.id);
        const newBook = [...Boook];
        newBook.splice(index, 1, action.payload);
        setEditableBook(null);
        return newBook;
      default:
        return Boook;
    }
  }
  function addBook(book) {
    // Send a POST request to add a new book
    axios.post("http://localhost:8000/book", book)
      .then(response => {
        dispatch({ type: "ADD", payload: response.data });
      })
      .catch(error => {
        console.error("Error adding book:", error);
      });
  }
  function deleteBook(id) {
    // Send a DELETE request to remove a book
    axios.delete(`http://localhost:8000/book/${id}`)
      .then(() => {
        dispatch({ type: "DELETE", payload: id });
      })
      .catch(error => {
        console.error("Error deleting book:", error);
      });
  }
  function updateBook(id) {
    setEditableBook(Boook.find((book) => book.id === id));
  }
  function editBook(book) {
    // Send a PUT request to update a book
    axios.put(`http://localhost:8000/book/${book.id}`, book)
      .then(response => {
        dispatch({ type: "EDIT", payload: response.data });
      })
      .catch(error => {
        console.error("Error editing book:", error);
      });
  }


  // function bookReducer(Boook, action) {
   
  //   switch (action.type) {
  //     case "ADD":
  //       return [...Boook, { ...action.payload, id: Boook.length + 1 }];
  //     case "Delete":
  //       return Boook.filter((book) => book.id !== action.payload);
  //     case "Edit":
  //       const index = Boook.findIndex((b) => b.id === action.payload.id);
  //       const newBook = [...Boook];
  //       newBook.splice(index, 1,action.payload);
  //       setEditableBook(null);
  //       return  newBook;
  //     default:
  //       return Boook;
  //   }
  // }


  // function updateBook(id) {
  //   console.log(id);
  //   setEditableBook(Boook.find((book) => book.id === id));
  // }


  return (
    <>
      <NavBar />
      <Box height={30} />
      <Box sx={{ display: "flex" }}>
        <SideNavBar />
        <Box component="main" sx={{ flexGrow: 1, p: 3 }}>
          <h1>Books</h1>
          <AddVideo
            addBook={addBook}
            editBook={editBook}
            editableBook={editableBook}
          />
          <BookList
            deleteBook={deleteBook}
            updateBook={updateBook}
            Boook={Boook}
          />
        </Box>
      </Box>
    </>
  );
}
export default Books;
 
