import React, { useState } from 'react';

function Book({
  Name,
  Author,
  Publisher,
  Availability,
  Ledger,
  id,
  dispatch,
  updateBook,
  img,
}) {
  const [selectedImage, setSelectedImage] = useState(img);

  const handleImageChange = (event) => {
    const file = event.target.files[0];
    if (file) {
      const reader = new FileReader();
      reader.onloadend = () => {
        setSelectedImage(reader.result);
      };
      reader.readAsDataURL(file);
    }
  };

  return (
    <>
      <div className="box">
        <div className="Pic">
          {selectedImage ? (
            <img src={selectedImage} alt="Book cover" />
          ) : (
            <div>Select an image</div>
          )}
        </div>
        <input
          type="file"
          accept="image/*"
          onChange={handleImageChange}
          style={{ margin: '10px 0' }}
        />
        <div className="book">{Name}</div>
        <div className="Author">Author: {Author}</div>
        <div className="Publisher">Publication: {Publisher}</div>
        <div className="Availability">Availability: {Availability}</div>
        <div className="Ledger">Ledger: {Ledger}</div>
        <button
          className="delete"
          onClick={() => dispatch({ type: 'Delete', payload: id })}
        >
          Delete
        </button>
        <button className="update" onClick={() => updateBook(id)}>
          Update
        </button>
      </div>
    </>
  );
}

export default Book;
