import SideNavBar from '../Components/SideNavBar';
import Box from '@mui/material/Box';
import NavBar from '../Components/NavBar';
import './Form.css';
import { useState } from 'react';

function Form({ addBook }) {
  const [book, setBook] = useState({ Avability: 'yes', Ledger: 'dew' });

  function handleSubmit(e) {
    e.preventDefault();
    addBook(book);
  }

  function handleChange(e) {
    setBook({ ...book, [e.target.name]: e.target.value });
  }

  return (
    <>
     <NavBar />
     <Box height={30} />
    <Box sx={{ display: "flex" }}>
       <SideNavBar />

        <Box component="main" sx={{ flexGrow: 1, p: 3 }}>
          <h1>Form</h1>
      <form>
           <input
             type="text"
               name="book"
              onChange={handleChange}
               placeholder="Book's Name"
             />
             <input
              type="text"
              name="author"
              onChange={handleChange}
              placeholder="Author's Name"
            />
            <input
              type="text"
              name="publisher"
              onChange={handleChange}
              placeholder="Publisher's Name"
            />
            {/* <input type="text" name="available" onChange={handleChange} placeholder="Available" />
            <input type="text" name="ledger" onChange={handleChange} placeholder="Ledger" /> */}
            <button onClick={handleSubmit}>Add Book</button>
            {/* <button onClick={()=>{
          
          setBoook(   [...Boook,{
            id:Boook.length+1,
            Name: "Demo in yourself",
            Author: "xyz",
            Publisher: "abc",
            Avability: "No",
            Ledger: "dew",
          }]);
        }}>Add Book</button> */}
           </form> */}
        </Box>
        </Box>
    </>
  );
}

export default Form;
