import { useState } from "react";

function AddVideo(){
    const [video, setVideo] = useState({Availability:"yes" , ledger: "yes"})

    function handleSubmit(){

    }

    function handleChange(e){
        console.log(e.target.name,e.target.value)
        setVideo({
            ...video,
            [e.target.name] : e.target.value
        } )
    }


    return(
        <>
        <form>
            <input type="text" name="Book's Name" onChange={handleChange} placeholder="Book's Name"/>
            <input type="text" name="Author's Name" onChange={handleChange} placeholder="Author's Name"/>
            <input type="text" name="Publication's Name" onChange={handleChange} placeholder="Publication's Name"/>
            <button onClick={handleSubmit}>Add Books</button>
        </form>
        </>
    );
}
export default AddVideo;
