import SideNavBar from "../Components/SideNavBar";
import NavBar from "../Components/NavBar";
import Box from "@mui/material/Box";
import axios from 'axios';
import { useReducer, useState,useEffect } from "react";
import AddVideo from "./AddVideo";
import BookList from "./BookList";

function Books() {
  const [editableBook, setEditableBook] = useState(null);
  const [Boook, dispatch] = useReducer(bookReducer, []);
  useEffect(() => {
    
    axios.get("http://localhost:8000/book")

      .then(response => {
        console.log(response)
        dispatch({ type: "SET_BOOKS", payload: response.data });
      })
      .catch(error => {
        console.error("Error fetching data:", error);
      });
  }, []);
  
   
  function bookReducer(Boook, action) {
    switch (action.type) {
      case "SET_BOOKS":
        return action.payload;
      case "ADD":
        return [...Boook, { ...action.payload, id: Boook.length + 1 }];
      // case "Delete":
      //   return Boook.filter((book) => book.id !== action.payload);
      case "DELETE":
        // Making a DELETE request to the backend API
        axios.delete(`http://localhost:8000/book/${action.payload}`)
          .then(() => {
            // On successful deletion from the backend, update the state
            dispatch({ type: "DELETE", payload: action.payload });
          })
          .catch(error => {
            console.error("Error deleting book:", error);
          });
        return Boook; // Return the current state before the API responds


      case "Edit":
        const index = Boook.findIndex((b) => b.id === action.payload.id);
        const newBook = [...Boook];
        newBook.splice(index, 1, action.payload);
        setEditableBook(null);
        return newBook;
      default:
        return Boook;
    }
  }

 

  function updateBook(id) {
    console.log(id);
    setEditableBook(Boook.find((book) => book.id === id));
  }

  return (
    <>
      <NavBar />
      <Box height={30} />
      <Box sx={{ display: "flex" }}>
        <SideNavBar />

        <Box component="main" sx={{ flexGrow: 1, p: 3 }}>
          <h1>Books</h1>
          <AddVideo
            dispatch={dispatch}
            // editBook={editBook}
            editableBook={editableBook}
          ></AddVideo>
          <BookList
            dispatch={dispatch}
            updateBook={updateBook}
            Boook={Boook}
          ></BookList>
        </Box>
      </Box>
    </>
  );
}
export default Books;
