import React, { useReducer, useState, useEffect } from "react";
import axios from 'axios';
import NavBar from "../Components/NavBar";
import SideNavBar from "../Components/SideNavBar";
import Box from "@mui/material/Box";
import AddVideo from "./AddVideo";
import BookList from "./BookList";

function Books() {
  const [editableBook, setEditableBook] = useState(null);
  const [Boook, dispatch] = useReducer(bookReducer, []);

  useEffect(() => {
    axios.get("http://localhost:8000/book")
      .then(response => {
        dispatch({ type: "SET_BOOKS", payload: response.data });
      })
      .catch(error => {
        console.error("Error fetching data:", error);
      });
  }, []);

  function bookReducer(Boook, action) {
    switch (action.type) {
      case "SET_BOOKS":
        return action.payload;
      case "ADD":
        return [...Boook, { ...action.payload, id: Boook.length + 1 }];
      case "DELETE":
        return Boook.filter((book) => book.id !== action.payload);
      case "EDIT":
        const index = Boook.findIndex((b) => b.id === action.payload.id);
        const newBook = [...Boook];
        newBook.splice(index, 1, action.payload);
        setEditableBook(null);
        return newBook;
      default:
        return Boook;
    }
  }

  function addBook(newBookData) {
    axios.post("http://localhost:8000/book", newBookData)
      .then(response => {
        dispatch({ type: "ADD", payload: response.data });
      })
      .catch(error => {
        console.error("Error adding book:", error);
      });
  }

  function deleteBook(id) {
    axios.delete(`http://localhost:8000/book/${id}`)
      .then(() => {
        dispatch({ type: "DELETE", payload: id });
      })
      .catch(error => {
        console.error("Error deleting book:", error);
      });
  }

  function updateBook(updatedBookData) {
    axios.put(`http://localhost:8000/book/${updatedBookData.id}`, updatedBookData)
      .then(response => {
        dispatch({ type: "EDIT", payload: response.data });
        setEditableBook(null);
      })
      .catch(error => {
        console.error("Error updating book:", error);
      });
  }

  return (
    <>
      <NavBar />
      <Box height={30} />
      <Box sx={{ display: "flex" }}>
        <SideNavBar />
        <Box component="main" sx={{ flexGrow: 1, p: 3 }}>
          <h1>Books</h1>
          <AddVideo
            dispatch={dispatch}
            editableBook={editableBook}
            addBook={addBook}
            updateBook={updateBook}
          />
          <BookList
            dispatch={dispatch}
            updateBook={updateBook}
            deleteBook={deleteBook}
            Boook={Boook}
          />
        </Box>
      </Box>
    </>
  );
}

export default Books;
