import { useEffect, useState } from "react";

const initialState = {
  Availability: "yes",
  ledger: "yes",
  Name: "",
  Author: "",
  Publisher: "",
  bookImage: null,
};

function AddVideo({ dispatch, editableBook }) {
  const [video, setVideo] = useState(initialState);
  // const [upload, setUpload] = useState(initialState);

  // function handleUpload(e){
  //   e.preventDefault();
  //   setUpload({
  //     ...video,
  //     [e.target.name]: e.target.value,
  //   });

  // }

  function handleSubmit(e) {
    e.preventDefault();
    if (editableBook) {
      dispatch({ type: "Edit", payload: video });
    } else {
      dispatch({ type: "ADD", payload: video });
    }

    setVideo(initialState);
  }

  function handleChange(e) {
    console.log(e.target.name, e.currentTarget);
    setVideo({
      ...video,
      // [e.target.name]: e.target.value,
        [e.target.name]: e.currentTarget.files[0]?.name || '' 
    });
  }

  useEffect(() => {
    if (editableBook) {
      setVideo(editableBook);
    }
  }, [editableBook]);

  return (
    <>
    {/* <form> */}
      <form action="/upload" method="post" encType="multipart/form-data" > 

      <input
          type="file"
          name="bookImage"
          onChange={handleChange}
          placeholder="Book"
          value={video.Name}
        />
        {/* <button onClick={handleUpload} >upload</button> */}
       {/* </form> */}

        <input
          type="text"
          name="Name"
          onChange={handleChange}
          placeholder="Book"
          value={video.Name}
        />

        <input
          type="text"
          name="Author"
          onChange={handleChange}
          placeholder="Author"
          value={video.Author}
        />
        <input
          type="text"
          name="Publisher"
          onChange={handleChange}
          placeholder="Publication"
          value={video.Publisher}
        />
        <button onClick={handleSubmit}>
          {editableBook ? "Update" : "Add"}Book
        </button>
      </form>
    </>
  );
}
export default AddVideo;
