import { useEffect, useState } from "react";
import axios from 'axios';
const initialState = {
  Availability: "yes",
  ledger: "yes",
  Name: "",
  Author: "",
  Publisher: "",
  bookImage: null,
};

function AddVideo({ dispatch, editableBook }) {
  const [video, setVideo] = useState(initialState);
  const [name, setName] = useState("");
  const [author, setAuthor] = useState("");
  const [publisher, setPublisher] = useState("");
  const [file, setFile] = useState("");

  axios.post('http://localhost:8000/book',formData)
  .then((response)=>{})


  const handleSubmit = async (e) => {
    e.preventDefault();
    if (editableBook) {
      dispatch({ type: "Edit", payload: video });
    } else {
      dispatch({ type: "ADD", payload: video });
      // console.log(video)
      // const formData = new FormData();
      // formData.append("image", video);

      // const response =  await fetch ('http://localhost:8000/book',{
      //   method: 'POST',

      //   body: formData,
      //   headers:{
      //     Accept:'multipart/form-data',
      //     'Content-Type': 'application/json'
      //   }
      // })
      // console.log(response);

     const formData = new FormData()
     formData.append("name", name);
     formData.append("author", author);
     formData.append("publisher",publisher );
     formData.append("file", file)

    



    }

    setVideo(initialState);
  };

  // function handleChange(e) {
  //   if (e.target.type === "file") {
  //     setVideo({
  //       ...video,
  //       [e.target.name]: e.target.files[0].name || null,
  //     });
  //   } else {
  //     setVideo({
  //       ...video,
  //       [e.target.name]: e.target.value,
  //     });
  //   }
  // }

  useEffect(() => {
    if (editableBook) {
      setVideo(editableBook);
    }
  }, [editableBook]);

  return (
    <>
      <form>
        <input
          accept="image/*"
          type="file"
          //name="img"
          onChange={(e)=> setFile(e.target.files[0])}
          placeholder="Book"
        />

        <input
          type="text"
         // name="Name"
          onChange={(e)=> setName(e.target.value)}
          placeholder="Book"
          value={video.Name}
        />

        <input
          type="text"
          //name="Author"
          onChange={(e)=> setAuthor(e.target.value)}
          placeholder="Author"
          value={video.Author}
        />
        <input
          type="text"
          //name="Publisher"
          onChange={(e)=> setPublisher(e.target.value)}
          placeholder="Publication"
          value={video.Publisher}
        />
        <button onClick={handleSubmit}>
          {editableBook ? "Update" : "Add"}Book
        </button>
      </form>
    </>
  );
}
export default AddVideo;
