import { useState, useEffect } from "react";
import "./Book.css";
import axios from 'axios';
import {Button, Dialog,DialogTitle,DialogContent,Stack,DialogActions,TextField} from "@mui/material";
function Book({ id, Name, Author, Publisher,Image,Availability,Ledger,deleteBook,}) {
  const initialState = {
    id: "",
    Image: "",
    Name: "",
    Author: "",
    Publisher: "",
    Availability: "",
    Ledger: "",
  };
  const [open, setOpen] = useState(false);
  const [video, setVideo] = useState(initialState);
  const [editableBook, setEditableBook] = useState(null);
  const [videos, setVideos] = useState([]);

  
  
  const fetchBook = () => {
    axios.get(`http://localhost:8000/book/${id}`)
      .then((response) => {
        console.log(response.data);
        setVideos(response.data);
      })
      .catch((error) => {
        console.error(error);
      });
  };

  useEffect(() => {
    fetchBook();
  }, []);

  const handleClickOpen = () => {
    setOpen(true);
    editBook(id);
  };
  const handleClose = () => {
    setOpen(false);
  };

  const editBook = (editdata) =>{
    setVideo(editdata);
    console.log(editdata)
  }

  const updateBook = (id) =>{
    const request = {
      method: "PUT",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify(video),
    };
    fetch(`http://localhost:8000/book/${id}`,request)
    .then((res)=>res.json())
    .then((json) =>{
      console.log(json);
      const newapi = [...videos, json];
      setVideos(newapi);
      fetchBook();
    })
    .catch((error)=> console.log(error));
  };


