//   const index = videos.findIndex((v) => v.id === updatedBook.id);
//   const newBooks = [...videos];
//   newBooks.splice(index, 1, updatedBook);

//   axios.put(`http://localhost:8000/book/${updatedBook.id}`, updatedBook, {
//     headers: {
//       "Content-Type": "application/json",
//     }
//   })
//   .then((response) => {
//     console.log(response.data);
//     setVideos(newBooks);
//     fetchBook();
//   })
//   .catch((error) => {
//     console.error(error);
//   });
// }

//   function handleSubmit(e) {
//     e.preventDefault();
//     if (editableBook) {
//       updateBook(video);
//     }
//     setVideo(initialState);
//     handleClose()
//   }
//   function handleChange(e) {
//     setVideo({ ...video, [e.target.name]: e.target.value });
//   }
//   useEffect(() => {
//     if (editableBook) {
      

//       setVideo(editableBook);
//     }
//   }, [editableBook]);

  
  return (
    <>
      <div className="grid-container">
        <div className="box">
          <div className="Pic">
            <img src={Image} alt="A book" />
          </div>
          <div className="book">{Name}</div>
          <div className="Author">Author: {Author}</div>
          <div className="Publisher">Publication: {Publisher}</div>
          <div className="Availability">Avability: {Availability}</div>
          <div className="Ledger">Ledger: {Ledger}</div>
          <button className="delete" onClick={() => deleteBook(id)}>
            Delete
          </button>
          <button
            color="primary"
            variant="contained"
            onClick={handleClickOpen}
          >
            Update
          </button>
          <Dialog open={open} onClose={handleClose} fullWidth>
            <DialogTitle>UserScreen</DialogTitle>
            <DialogContent>
              <Stack spacing={2} margin={2}>
                <TextField variant="outlined" label="Update Book Name"  onChange={(e)=> setVideo({...video, Name: e.target.value})} value={video.Name}></TextField>
                <TextField variant="outlined" label="Update Author Name"  onChange={(e)=> setVideo({...video, Author: e.target.value})}value={video.Author}></TextField>
                <TextField variant="outlined" label="Update Publisher Name"  onChange={(e)=> setVideo({...video, Publisher: e.target.value})}value={video.Publisher} ></TextField>
                <TextField variant="outlined" label="Update Availability"  onChange={(e)=> setVideo({...video, Availability: e.target.value})} value={video.Availability} ></TextField>
                <TextField variant="outlined" label="Update Ledger" onChange={(e)=> setVideo({...video, Ledger: e.target.value})}  value={video.Ledger}></TextField>
                <Button color="primary" variant="contained" onClick={()=>updateBook(video.id)}>Update</Button>
              </Stack>
            </DialogContent>
            <DialogActions>
              <Button color="error" variant="contained" onClick={handleClose}>Close</Button>
            </DialogActions>
          </Dialog>
        </div>
      </div>
    </>
  );
}
export default Book;
