import { useState } from 'react';
import defaultImage from "../image/i8.PNG";
import "./Book.css";

function Book({
  Name,
  Author,
  Publisher,
  Availability,
  Ledger,
  id,
  dispatch,
  updateBook,
  img,
}) {
  const [uploadedImage, setUploadedImage] = useState(null);

  const handleImageUpload = (event) => {
    const file = event.target.files[0];
    const reader = new FileReader();

    reader.onloadend = () => {
      setUploadedImage(reader.result);
    };

    if (file) {
      reader.readAsDataURL(file);
    }
  };

  return (
    <>
      <div className="box">
        <div className="Pic">
          {uploadedImage ? (
            <img src={uploadedImage} alt="Uploaded book" />
          ) : (
            <img src={img || defaultImage} alt="Default book" />
          )}
          <input
            type="file"
            accept="image/*"
            onChange={handleImageUpload}
            className="image-upload"
          />
        </div>
        <div className="book">{Name}</div>
        <div className="Author">Author: {Author}</div>
        <div className="Publisher">Publication: {Publisher}</div>
        <div className="Availability">Availability: {Availability}</div>
        <div className="Ledger">Ledger: {Ledger}</div>
        <button
          className="delete"
          onClick={() => dispatch({ type: "Delete", payload: id })}
        >
          Delete
        </button>
        <button className="update" onClick={() => updateBook(id)}>
          Update
        </button>
      </div>
    </>
  );
}

export default Book;
