import { useEffect, useState } from "react";

const initialState = {
  Availability: "yes",
  ledger: "yes",
  Name: "",
  Author: "",
  Publisher: "",
  bookImage: null,
};

function AddVideo({ dispatch, editableBook }) {
  const [video, setVideo] = useState(initialState);
  

  const handleSubmit = async(e)=> {
    e.preventDefault();
    if (editableBook) {
      dispatch({ type: "Edit", payload: video });
    } else {
      dispatch({ type: "ADD", payload: video });
      console.log(video)
      const formData = new FormData();
      formData.append("image", video);
  
      const response =  await fetch ('http://localhost:8000/book',{
        method: 'POST',
    
        body: formData,
        headers:{
          Accept:'multipart/form-data',
          'Content-Type': 'application/json'
        }
      })
      console.log(response);
        }
    
    setVideo(initialState);
  }

  // function handleChange(e) {
  //   if (e.target.type === 'file') {
  //     setVideo({
  //       ...video,
  //       [e.target.name]:  e.target.files[0]|| '' 
  //     });
  //   } else {
  //     setVideo({
  //       ...video,
  //       [e.target.name]: e.target.value
  //     });
  //   }
  // }

function handleChange(e) {
  if (e.target.type === 'file') {
    setVideo({
      ...video,
      bookImage: e.target.files[0], // Set the 'bookImage' property with the selected file
      [e.target.name]: e.target.files[0]?.name || null, // Optionally set the file name for display
    });
  } else {
    setVideo({
      ...video,
      [e.target.name]: e.target.value,
    });
  }
}

  
  

  useEffect(() => {
    if (editableBook) {
      setVideo(editableBook);
    }
  }, [editableBook]);


  // const[image,setImage] = useState("");

  function convertToBase64(e) {
    var reader = new FileReader();
    reader.readAsDataURL(e.target.files[0]);
    reader.onload = () => {
      setVideo({
        ...video,
        bookImage: reader.result 
      });
    };
    reader.onerror = error => {
      console.log("Error", error);
    };
  }
  

  return (
    <>
    <form>
     

      <input
          accept="image/*"
          type="file"
          name="img"
          onChange={convertToBase64}
          // onSubmit={handleSubmit}
          placeholder="Book"
          
        />
        {/* {image==""||image==null?"":<img src={image}/>} */}
        
       
        <input
          type="text"
          name="Name"
          onChange={handleChange}
          placeholder="Book"
          value={video.Name}
        />

        <input
          type="text"
          name="Author"
          onChange={handleChange}
          placeholder="Author"
          value={video.Author}
        />
        <input
          type="text"
          name="Publisher"
          onChange={handleChange}
          placeholder="Publication"
          value={video.Publisher}
        />
        <button onClick={handleSubmit}>
          {editableBook ? "Update" : "Add"}Book
        </button>
      </form>
    </>
  );
}
export default AddVideo;
