import { useState } from "react";

function AddVideo({addVideos}) {
  const [video, setVideo] = useState({ Availability: "yes", ledger: "yes" });

  function handleSubmit(e) {
    e.preventDefault();
    addVideos(video)
    console.log(video)
  }

  function handleChange(e) {
    console.log(e.target.name, e.target.value);
    setVideo({
      ...video,
      [e.target.name]: e.target.value,
    });
    
  }

  return (
    <>
      <form>
        <input
          type="text"
          name="Book"
          onChange={handleChange}
          placeholder="Book"
          value={video}
        /> 
       
        <input
          type="text"
          name="Author"
          onChange={handleChange}
          placeholder="Author"
        />
        <input
          type="text"
          name="Publication"
          onChange={handleChange}
          placeholder="Publication"
        />
        <button onClick={handleSubmit}>Add Books</button>
      </form>
    </>
  );
}
export default AddVideo;
