import SideNavBar from "../Components/SideNavBar";
import NavBar from "../Components/NavBar";
import Box from "@mui/material/Box";
import Book from "./Book";
import obj from "../data/Data";
import { useState } from "react";
import { useNavigate } from "react-router-dom";
import Form from "./Form";

function Books() {
  
  const[Boook,setBoook] = useState(obj)

  function addBook({add}){
    
    setBoook(   [...Boook,
      {...book, id:Boook.length+1 }]);
  }
 
      
  const navigate = useNavigate();
  const handleClick = () =>{
    navigate("/Form")
  }
    

//     const navigate = useNavigate();
//     const handleClick = () => {
// navigate('/target', { state: {  Avability: "yes", Ledger: "dew"  } })
//   }
  return (
    <>
      <NavBar />
      <Box height={30} />
      <Box sx={{ display: "flex" }}>
        <SideNavBar />

        <Box component="main" sx={{ flexGrow: 1, p: 3 }}>
          <h1>Books</h1>

          {Boook.map((book) => (
            <Book
              key={book.id}
              Name={book.Name}
              Author={book.Author}
              Publisher={book.Publisher}
              Avability={book.Avability}
              Ledger={book.Ledger}
            />
          ))}
          <div>
          <button onClick={handleClick}>Add Books</button>
          </div>
          {console.log(addBook)}
          {/* <Form addBook={(book)=>addBook(book)}/> */}
          <Form addBook = {addBook}/>
        </Box>
      </Box>
    </>
  );
}
export default Books;
