import { useState } from "react";

function AddVideo({ addVideos }) {
  const [video, setVideo] = useState({ Availability: "yes", ledger: "yes", Name:"",});

  function handleSubmit(e) {
    e.preventDefault();
    addVideos(video);
    console.log(video);
  }

  function handleChange(e) {
    console.log(e.target.name, e.target.value);
    setVideo({
      ...video,
      [e.target.name]: e.target.value,
    });
  }

  return (
    <>
      <form>
        <input
          type="text"
          name="Name"
          onChange={handleChange}
          placeholder="Book"
          value={video.Name}
        />

        <input
          type="text"
          name="Author"
          onChange={handleChange}
          placeholder="Author"
          value={video.Author}
        />
        <input
          type="text"
          name="Publisher"
          onChange={handleChange}
          placeholder="Publication"
          value={video.Publisher}
        />
        <button onClick={handleSubmit}>Add Books</button>
      </form>
    </>
  );
}
export default AddVideo;
