import { useState } from "react";

function AddVideo() {
  const [video, setVideo] = useState({ Availability: "yes", ledger: "yes" });

  function handleSubmit() {}

  function handleChange(e) {
    console.log(e.target.name, e.target.value);
    setVideo({
      ...video,
      [e.target.name]: e.target.value,
    });
    console.log(video);
  }

  return (
    <>
      <form>
        <input
          type="text"
          name="Book "
          onChange={handleChange}
          placeholder="Book"
        />
        {video.Book}
        <input
          type="text"
          name="Author "
          onChange={handleChange}
          placeholder="Author"
        />
        <input
          type="text"
          name="Publication "
          onChange={handleChange}
          placeholder="Publication"
        />
        <button onClick={handleSubmit}>Add Books</button>
      </form>
    </>
  );
}
export default AddVideo;
