import { useEffect, useState } from "react";

const initialState = {
  Availability: "yes",
  ledger: "yes",
  Name: "",
  Author: "",
  Publisher: "",
  bookImage: null,
};

function AddVideo({ dispatch, editableBook }) {
  const [video, setVideo] = useState(initialState);
  

  const handleSubmit = async(e)=> {
    e.preventDefault();
    if (editableBook) {
      dispatch({ type: "Edit", payload: video });
    } else {
      dispatch({ type: "ADD", payload: video });
      const response =  await fetch ('http://localhost:8000/book',{
        method: 'POST',
      })
      
        }
    
    setVideo(initialState);
  }

  function handleChange(e) {
    if (e.target.type === 'file') {
      setVideo({
        ...video,
        [e.target.name]:  e.target.files[0]|| '' 
      });
    } else {
      setVideo({
        ...video,
        [e.target.name]: e.target.value
      });
    }
  }
  

  useEffect(() => {
    if (editableBook) {
      setVideo(editableBook);
    }
  }, [editableBook]);

  return (
    <>
    <form>
     

      <input
          type="file"
          name="bookImage"
          onChange={handleChange}
          placeholder="Book"
          
        />
       
        <input
          type="text"
          name="Name"
          onChange={handleChange}
          placeholder="Book"
          value={video.Name}
        />

        <input
          type="text"
          name="Author"
          onChange={handleChange}
          placeholder="Author"
          value={video.Author}
        />
        <input
          type="text"
          name="Publisher"
          onChange={handleChange}
          placeholder="Publication"
          value={video.Publisher}
        />
        <button onClick={handleSubmit}>
          {editableBook ? "Update" : "Add"}Book
        </button>
      </form>
    </>
  );
}
export default AddVideo;
