import { useEffect, useState } from "react";

function AddVideo({editableBook,updateBook}){
    const[video, setVideo] = useState(initialState)

    function handleSubmit(e){
      e.preventDefault()
      if(editableBook){
        updateBook(video)
      }
      setVideo(initialState)
    }
    function handleChange(e){
      
      setVideo({...video,[e.target.name]:e.target.value})
    }

    useEffect(()=>{
        if(editableBook){
            setVideo(editableBook)
        }
       
      
    },[editableBook])

    return(
        <>
        <form>
        <input type="text" name="Name" onChange={handleChange}
             value={video.Name}
             />
             <input type="text" name="Author" onChange={handleChange}
             value={video.Author}
             />
             <input type="text" name="Publisher" onChange={handleChange}
             value={video.Publisher}
             />
            <input type="text" name="Availability" onChange={handleChange}
             value={video.Availability}
             />
            <input type="text" name= "Ledger" onChange={handleChange}
             value= {video.Ledger}
             />
            <button onClick={handleSubmit}>Update</button>
        </form>
        </>
    )
}
export default AddVideo;
