import i8 from "../image/i8.PNG";
import "./Book.css";

function Book({ Name, Author, Publisher, Availability, Ledger,id,dispatch,updateBook }) {
  return (
    <>
      <div className="box"> 
        <div className="Pic">
          <img src={img} alt="A book" />
        </div>
        <div className="book">{Name}</div>
        <div className="Author">Author:{Author}</div>
        <div className="Publisher">Publication:{Publisher}</div>
        <div className="Availability"> Avability:{Availability}</div>
        <div className="Ledger">Ledger:{Ledger}</div>
        <button className="delete" onClick={()=>dispatch({ type: "Delete", payload: id })}>Delete</button>
        <button className="update" onClick={()=>updateBook(id)}>Update</button>
      </div>
    </>
  );
}
export default Book;
