import i8 from "../image/i8.PNG";
import { useState } from "react";
import "./Book.css";
import { Button, Dialog, DialogTitle, DialogContent, Stack, DialogActions,TextField } from '@mui/material';
function Book({id,Name, Author, Publisher,Image, Availability,Ledger,deleteBook,editBook}) {
  const [open, setOpen] = useState(false);

  const handleClickOpen = () => {
    setOpen(true);
  };

  const handleClose = () => {
    setOpen(false);
  };

  const[video, setVideo] = useState(initialState)

  function handleSubmit(e){
    e.preventDefault()
    if(editableBook){
      updateBook(video)
    }
    setVideo(initialState)
  }
  function handleChange(e){
    
    setVideo({...video,[e.target.name]:e.target.value})
  }

  useEffect(()=>{
      if(editableBook){
          setVideo(editableBook)
      }
     
    
  },[editableBook])


  
  return (
    <>
      <div className="box">
        <div className="Pic">
           {/* <img src={i8} alt="A book" /> */}
          <img src = {Image} alt="A book"/>
        </div>
        <div className="book">{Name}</div>
        <div className="Author">Author:{Author}</div>
        <div className="Publisher">Publication:{Publisher}</div>
        <div className="Availability"> Avability:{Availability}</div>
        <div className="Ledger">Ledger:{Ledger}</div>
        <button
          className="delete"onClick={()=>deleteBook(id)}>
          Delete
        </button>
        {/* <button className="update" onClick = {()=>editBook(id)}>
          Update
        </button> */}
           <button color="primary" variant="contained" onClick={handleClickOpen}>Update</button>
          <Dialog open={open} onClose={handleClose} fullWidth>
            <DialogTitle>UserScreen</DialogTitle>
            <DialogContent>
                
                <Stack spacing={2} margin={2}>
                 <TextField variant="outlined" label = " Update Book Name" ></TextField>
                 <TextField variant="outlined" label = " Update Author Name"></TextField>
                 <TextField variant="outlined" label = " Update Publisher Name"></TextField>
                 <TextField variant="outlined" label = " Update Availability"></TextField>
                 <TextField variant="outlined" label = " Update Ledger"></TextField>

                 <Button color="primary" variant="contained" >Update</Button>
                </Stack>
            </DialogContent>
            <DialogActions>
               
                <Button color="error" variant="contained" onClick={handleClose}>Close</Button>
            </DialogActions>
          </Dialog>

      </div>
    </>
  );
}
export default Book;
