import axios from 'axios';
import { useNavigate } from "react-router-dom";
import Validation from './LoginValidation';
import "./Login.css";
import { useState } from "react";

function Login() {
  const [value, setValue] = useState({
    "email": '',
    "password": ''
  });
  const [error, setError] = useState({});

  function handleInput(e) {
    setValue(prev => ({ ...prev, [e.target.name]: e.target.value }));
  }

  function handleSubmit(e) {
    e.preventDefault();
    setError(Validation(value));

    if(error.email===""&&error.password===""){
      axios.post('http://localhost:8000/login',value)
      .then(res=> {
        if(res.data==="success"){
          navigate('/Books');
      }else{
        alert("NO record exists")
      }
    })
      .catch(err=> console.log(err));

    }

  }

  const navigate = useNavigate();

  return (
    <>
      <div className="Container">
        <div className="Header">
          <div className="text">Login</div>
          <div className="underline"></div>
        </div>
        <form className="inputs" onSubmit={handleSubmit}>
          <div className="input">
            <input type="email" name="email" placeholder="Enter Your Email" onChange={handleInput} />
            {error.email && <span>{error.email}</span>}
            <input type="password" name="password" placeholder="Enter Your Password" onChange={handleInput} />
            {error.password && <span>{error.password}</span>}
          </div>
          <div className="Submit-container">
            <button type="submit" className="Submit"  onClick={() => navigate("/Books")}>Login</button>
            <button className="Submit" onClick={() => navigate("/")}>Create Your account</button>
          </div>
        </form>
      </div>
    </>
  );
}

export default Login;
