import SideNavBar from "../Components/SideNavBar";
import NavBar from "../Components/NavBar";
import Box from "@mui/material/Box";
import axios from "axios";
//import obj from "../data/Data";
import { useReducer, useState, useEffect} from "react";
import AddVideo from "./AddVideo";
import BookList from "./BookList";
//import { useNavigate } from "react-router-dom";
// import Form from "./Form";

function Books() {
  const [editableBook, setEditableBook] = useState(null);
  //const [Boook, dispatch] = useReducer(bookReducer, obj);
  const [Boook, dispatch] = useReducer(bookReducer, []);

  useEffect(() => {
    // Fetch data from the API
    axios.get("http://localhost:8000/book")
      .then(response => {
        dispatch({ type: "SET_BOOKS", payload: response.data });
      })
      .catch(error => {
        console.error("Error fetching data:", error);
      });
  }, []);

  function bookReducer(Boook, action) {
   
    switch (action.type) {
      case "ADD":
        return [...Boook, { ...action.payload, id: Boook.length + 1 }];
      case "Delete":
        return Boook.filter((book) => book.id !== action.payload);
      case "Edit":
        const index = Boook.findIndex((b) => b.id === action.payload.id);
        const newBook = [...Boook];
        newBook.splice(index, 1,action.payload);
        setEditableBook(null);
        return  newBook;
      default:
        return Boook;
    }
  }

 
  // const[Boook,setBoook] = useState(obj)


  // function addVideos(video) {
  //   dispatch({ type: "ADD", payload: video });
  //   //action: {type:'ADD',payload:video}

  //   //  setBoook([...Boook,
  //   //     { ...video, id:Boook.length+1}
  //   // ])
  // }

  // function deleteBook(id) {
  //   dispatch({ type: "Delete", payload: id });

  //   //setBoook(Boook.filter(book=>book.id!==id))
  // }
  function updateBook(id) {
    console.log(id);
    setEditableBook(Boook.find((book) => book.id === id));
  }

 
  return (
    <>
      <NavBar />
      <Box height={30} />
      <Box sx={{ display: "flex" }}>
        <SideNavBar />

        <Box component="main" sx={{ flexGrow: 1, p: 3 }}>
          <h1>Books</h1>
          <AddVideo
            dispatch={dispatch}
            // editBook={editBook}
            editableBook={editableBook}
          ></AddVideo>
          <BookList
            dispatch={dispatch}
            updateBook={updateBook}
            Boook={Boook}
          ></BookList>
        </Box>
      </Box>
    </>
  );
}
export default Books;
