import { useNavigate } from "react-router-dom";
import  '';
import { useState } from "react";
import "./Login.css";
function Login() {
  const [value, setValue] = useState({
    email: "",
    password: "",
  });

    function handleInput(e){
    e.preventDefault();
    setValue(prev=>({...prev, [e.target.name] : [e.target.value]}))
  }

    function handleSubmit(e){
        e.preventDefault();
        setValue(validation(value))
    }

//   const navigate = useNavigate();

  return (
    <>
      <div className="Container">
        <div className="Header">
          <div className="text">Login</div>
          <div className="underline"></div>
        </div>
        <div className="inputs">
          <div className="input">
            {/* <input type="text" placeholder='Enter Your Name'/> */}
            <input onChange={handleInput} type="email" placeholder="Enter Your Email"  name="email"/>
            <input onChange={handleInput} type="password" placeholder="Enter Your Password" name="password" />
          </div>
        </div>
        <div className="Submit-container">
          <div  type = "submit" className="Submit" onSubmit={handleSubmit}
          // onClick={() => navigate("/Books")}
          >
            Login
          </div>
        </div>
      </div>

      {/* <button onClick={handleClick}>Login</button> */}
    </>
  );
}
export default Login;
