import { useEffect, useState } from "react";
//import axios from "axios";
const initialState = {
  Availability: "yes",
  ledger: "yes",
  Name: "",
  Author: "",
  Publisher: "",
  bookImage: null,
};

function AddVideo({ dispatch, editableBook }) {
  const [video, setVideo] = useState(initialState);
  // const [upload, setUpload] = useState(initialState);

  // function handleUpload(e){
  //   e.preventDefault();
  //   setUpload({
  //     ...video,
  //     [e.target.name]: e.target.value,
  //   });

  // }

  const handleSubmit = async(e)=> {
    e.preventDefault();
    if (editableBook) {
      dispatch({ type: "Edit", payload: video });
    } else {
      dispatch({ type: "ADD", payload: video });
     const response = await fetch("http://localhost:8000/book",{method: "POST"})
     console.log(response)
    }
    
    setVideo(initialState);
  }

  function handleChange(e) {
    if (e.target.type === 'file') {
      setVideo({
        ...video,
        [e.target.name]:  e.target.files[0]|| '' 
      });
    } else {
      setVideo({
        ...video,
        [e.target.name]: e.target.value
      });
    }
  }
  

  useEffect(() => {
    if (editableBook) {
      setVideo(editableBook);
    }
  }, [editableBook]);

  return (
    <>
    <form>
      {/* <form action="/upload" method="post" encType="multipart/form-data" >  */}

      <input
          type="file"
          name="bookImage"
          onChange={handleChange}
          placeholder="Book"
          
        />
        {/* <button onClick={handleUpload} >upload</button> */}
       {/* </form> */}

        <input
          type="text"
          name="Name"
          onChange={handleChange}
          placeholder="Book"
          value={video.Name}
        />

        <input
          type="text"
          name="Author"
          onChange={handleChange}
          placeholder="Author"
          value={video.Author}
        />
        <input
          type="text"
          name="Publisher"
          onChange={handleChange}
          placeholder="Publication"
          value={video.Publisher}
        />
        <button onClick={handleSubmit}>
          {editableBook ? "Update" : "Add"}Book
        </button>
      </form>
    </>
  );
}
export default AddVideo;
