import {Button} from '@mui/material';
import "./Book.css";

function Book({
      Name,
      Author,
      Publisher,
      Availability,
      Ledger,
      id,
      Image,
    }) {
  return (
    <>
      <div className="box">
        <div className="Pic">
                   <img src = {Image} alt="A book"/>
        </div>
        <div className="book">{Name}</div>
        <div className="Author">Author:{Author}</div>
        <div className="Publisher">Publication:{Publisher}</div>
        <div className="Availability"> Avability:{Availability}</div>
        <div className="Ledger">Ledger:{Ledger}</div>
        <Button color="error" variant="contained" onClick={()=>deleteBook(id)}>Delete</Button>
        <Button color="success" variant="contained">Update</Button>
            
      </div>
    </>
  );
}
export default Book;
