import { useEffect, useState } from "react";

const initialState = {
  Availability: "yes",
  ledger: "yes",
  Name: "",
  Author: "",
  Publisher: "",
  bookImage: null,
};

function AddVideo({ dispatch, editableBook }) {
  const [video, setVideo] = useState(initialState);
  

  // const handleSubmit = async(e)=> {
  //   e.preventDefault();
  //   if (editableBook) {
  //     dispatch({ type: "Edit", payload: video });
  //   } else {
  //     dispatch({ type: "ADD", payload: video });
  //     console.log(video)
  //     const formData = new FormData();
  //     formData.append("image", video);
  
  //     const response =  await fetch ('http://localhost:8000/book',{
  //       method: 'POST',
    
  //       body: formData,
  //       headers:{
  //         Accept:'multipart/form-data',
  //         'Content-Type': 'application/json'
  //       }
  //     })
  //     console.log(response);
  //       }
    
  //   setVideo(initialState);
  // }

  // function handleChange(e) {
  //   if (e.target.type === 'file') {
  //     setVideo({
  //       ...video,
  //       [e.target.name]:  e.target.files[0]|| '' 
  //     });
  //   } else {
  //     setVideo({
  //       ...video,
  //       [e.target.name]: e.target.value
  //     });
  //   }
  // }

  function handleChange(e) {
    if (e.target.type === 'file') {
      setVideo({
        ...video,
        bookImage: e.target.files[0] || null,
        // [e.target.name]: e.target.files[0].name || null 
      });
    } else {
      setVideo({
        ...video,
        [e.target.name]: e.target.value
      });
    }
  }
  
  

  useEffect(() => {
    if (editableBook) {
      setVideo(editableBook);
    }
  }, [editableBook]);

  return (
    <>
    <form onSubmit={handleSubmit}>
     

      <input
          type="file"
          name="img"
          onChange={handleChange}
          placeholder="Book"
          
        />
       
        <input
          type="text"
          name="Name"
          onChange={handleChange}
          placeholder="Book"
          value={video.Name}
        />

        <input
          type="text"
          name="Author"
          onChange={handleChange}
          placeholder="Author"
          value={video.Author}
        />
        <input
          type="text"
          name="Publisher"
          onChange={handleChange}
          placeholder="Publication"
          value={video.Publisher}
        />
        {/* <button onClick={handleSubmit}>
          {editableBook ? "Update" : "Add"}Book
        </button> */}
         <button type="submit">
        {editableBook ? "Update" : "Add"} Book
      </button>
      </form>
    </>
  );
}
export default AddVideo;
