import Book from "./Book";

function BookList({ videos, deleteBook, editBook, setVideos }) {
  // Function to update the videos list
  const updateVideos = (updatedVideo) => {
    const updatedVideos = videos.map((video) =>
      video.id === updatedVideo.id ? updatedVideo : video
    );
    setVideos(updatedVideos);
  };

  return (
    <div className="grid-container">
      {videos.map((video) => (
        <Book
          key={video.id}
          id={video.id}
          Image={video.Image}
          Name={video.Name}
          Author={video.Author}
          Publisher={video.Publisher}
          Availability={video.Availability}
          Ledger={video.Ledger}
          deleteBook={deleteBook}
          editBook={editBook}
          videos={videos} // Pass the videos list to Book component
          setVideos={updateVideos} // Pass the update function to Book component
        />
      ))}
    </div>
  );
}

export default BookList;
