import Book from "./Book";
import { useState,useEffect } from "react";
import axios from "axios";
function BookList({deleteBook,editBook}){
    const [videos, setVideos] = useState([]);

    useEffect(() => {
        // Fetch book data from http://localhost:8000/book
        axios.get('http://localhost:8000/book')
          .then((response) => {
            setVideos(response.data); // Update state with fetched book data
          })
          .catch((error) => {
            console.error('Error fetching books:', error);
          });
      }, []);

    return(
        <>
         <div className="grid-container">
        {videos.map(video=><Book 
                 key = {video.id}
                 id = {video.id}
                 Image = {video.Image}
                 Name = {video.Name}
                 Author = {video.Author}
                 Publisher = {video.Publisher}
                 Availability = {video.Availability}
                 Ledger = {video.Ledger}
                 deleteBook = {deleteBook}
                 editBook={editBook}
                 >

                 </Book>)}
                 </div>
        </>
    )
}
export default BookList;
