const initialState = {
  Availability: "yes",
  ledger: "yes",
  img: "",
  Name: "",
  Author: "",
  Publisher: "",
  bookImage: null,
};

function AddVideo({ dispatch, editableBook }) {
  const [video, setVideo] = useState(initialState);
  

  const handleSubmit = async(e)=> {
    e.preventDefault();
    if (editableBook) {
      dispatch({ type: "Edit", payload: video });
