  },
  formData: {
    'Name': 'Why I am ',
    'Publisher': 'Peguin',
    'Author': 'Shashi Tharor',
    'Image': {
      'value': fs.createReadStream('/C:/Users/Ayushi/Pictures/The monk who.PNG'),
      'options': {
        'filename': '/C:/Users/Ayushi/Pictures/The monk who.PNG',
        'contentType': null
      }
    }
  }
};
request(options, function (error, response) {
  if (error) throw new Error(error);
  console.log(response.body);
});
  
