        body: formData,
        headers:{
          Accept:'multipart/form-data',
        }
      })
      console.log(response);
        }
    
    setVideo(initialState);
  }

  // function handleChange(e) {
  //   if (e.target.type === 'file') {
  //     setVideo({
  //       ...video,
  //       [e.target.name]:  e.target.files[0]|| '' 
  //     });
  //   } else {
  //     setVideo({
  //       ...video,
  //       [e.target.name]: e.target.value
  //     });
  //   }
  // }

  function handleChange(e) {
    if (e.target.type === 'file') {
      setVideo({
        ...video,
        [e.target.name]: e.target.files[0].name || null 
      });
    } else {
      setVideo({
        ...video,
        [e.target.name]: e.target.value
      });
    }
  }
  
  

  useEffect(() => {
    if (editableBook) {
      setVideo(editableBook);
    }
  }, [editableBook]);

  return (
    <>
    <form>
     

      <input
          type="file"
          name="img"
          onChange={handleChange}
          placeholder="Book"
          
        />
       
        <input
          type="text"
          name="Name"
          onChange={handleChange}
          placeholder="Book"
          value={video.Name}
        />

        <input
          type="text"
          name="Author"
          onChange={handleChange}
          placeholder="Author"
          value={video.Author}
        />
        <input
          type="text"
          name="Publisher"
          onChange={handleChange}
          placeholder="Publication"
          value={video.Publisher}
        />
        <button onClick={handleSubmit}>
          {editableBook ? "Update" : "Add"}Book
        </button>
      </form>
    </>
  );
}
export default AddVideo;
