import SideNavBar from "../Components/SideNavBar";
import NavBar from "../Components/NavBar";
import Box from "@mui/material/Box";

import obj from "../data/Data";
import { useReducer, useState } from "react";
import AddVideo from "./AddVideo";
import BookList from "./BookList";
//import { useNavigate } from "react-router-dom";
// import Form from "./Form";

function Books() {

 function bookReducer(Boook,action){
  switch(action.type){
    case 'ADD':
      return [...Boook,
        { ...action.payload, id:Boook.length+1}
    ]
    
      default:
        return Boook;
  }

 }

   const[Boook,dispatch] = useReducer(bookReducer,obj)
  // const[Boook,setBoook] = useState(obj)
   const[editableBook, setEditableBook] = useState(null)

   function addVideos(video){
    dispatch({type:'ADD',payload:video})
    //action: {type:'ADD',payload:video}

    //  setBoook([...Boook,
    //     { ...video, id:Boook.length+1}
    // ])
    
   }

   function deleteBook(id){
    dispatch({type:'Delete',payload:id})
   
   //setBoook(Boook.filter(book=>book.id!==id))
   
   
  }
  function updateBook(id){
   console.log(id)
   setEditableBook(Boook.find(book=>book.id===id))
  }

  function editBook(video){
    const index = Boook.findIndex(b=>b.id===video.id)
    const newBook =  [...Boook]
    newBook.splice(index,1,video)
    //setBoook(newBook);
  }

  // function addBook({book}){
    
  //   setBoook(   [...Boook,
  //     {...book, id:Boook.length+1 }]);
  // }
 
      
  // const navigate = useNavigate();
  // const handleClick = () =>{
  //   navigate("/Form")
  // }
    

//     const navigate = useNavigate();
//     const handleClick = () => {
// navigate('/target', { state: {  Avability: "yes", Ledger: "dew"  } })
//   }
  return (
    <>
      <NavBar />
      <Box height={30} />
      <Box sx={{ display: "flex" }}>
        <SideNavBar />

        <Box component="main" sx={{ flexGrow: 1, p: 3 }}>
          <h1>Books</h1>
          <AddVideo addVideos = {addVideos} editBook={editBook} editableBook={editableBook}></AddVideo>
          <BookList deleteBook={deleteBook} updateBook={updateBook} Boook = {Boook}></BookList>
        </Box>
      </Box>
    </>
  );
}
export default Books;
