import Book from "./Book";
import { useEffect, useState } from 'react';
function BookList({videos,deleteBook,editBook}){
    const [books, setBooks] = useState([]);
    useEffect(() => {
        const params = new URLSearchParams(window.location.search);
        const authorName = params.get('author');
    
        // Fetch books related to the selected author
        fetch(`http://localhost:8000/booksByAuthor?author=${encodeURIComponent(authorName)}`)
          .then((response) => response.json())
          .then((data) => {
            setBooks(data.books); // Set the fetched books in state
          })
          .catch((error) => {
            console.error('Error fetching books:', error);
          });
      }, []);
    


    return(
        <>
         <div className="grid-container">
        {videos.map(video=><Book 
                 key = {video.id}
                 id = {video.id}
                 Image = {video.Image}
                 Name = {video.Name}
                 Author = {video.Author}
                 Publisher = {video.Publisher}
                 Availability = {video.Availability}
                 Ledger = {video.Ledger}
                 deleteBook = {deleteBook}
                 editBook={editBook}
                 >

                 </Book>)}
                 </div>
        </>
    )
}
export default BookList;
