import i8 from "../image/i8.PNG";
import "./Book.css";
import axios from "axios";
function Book({ Name, Author, Publisher, Availability, Ledger, id, dispatch, updateBook }) {
  const handleDelete = () => {
    // Send a DELETE request to remove the book
    axios.delete(`http://localhost:8000/book/${id}`)
      .then(() => {
        dispatch({ type: "Delete", payload: id });
      })
      .catch(error => {
        console.error("Error deleting book:", error);
      });
  };
  const handleUpdate = () => {
    // Fetch the book details from the API
    axios.get(`http://localhost:8000/book/${id}`)
      .then(response => {
        updateBook(response.data);
      })
      .catch(error => {
        console.error("Error fetching book details for update:", error);
      });
  };
  return (
    <>
      <div className="box">
        <div className="Pic">
          <img src={i8} alt="A book" />
        </div>
        <div className="book">{Name}</div>
        <div className="Author">Author: {Author}</div>
        <div className="Publisher">Publication: {Publisher}</div>
        <div className="Availability">Availability: {Availability}</div>
        <div className="Ledger">Ledger: {Ledger}</div>
        <button className="delete" onClick={handleDelete}>Delete</button>
        <button className="update" onClick={handleUpdate}>Update</button>
      </div>
    </>
  );
}
export default Book;
