import { useEffect, useState } from "react";
const initialState =     { Availability: "yes", ledger: "yes", Name:"", Author:"",Publisher:""}   

function AddVideo({ addVideos,editableBook }) {
  const [video, setVideo] = useState(initialState);

  function handleSubmit(e) {
    e.preventDefault();
    addVideos(video);
    console.log(video);
    setVideo(initialState)
  }

  function handleChange(e) {
    console.log(e.target.name, e.target.value);
    setVideo({
      ...video,
      [e.target.name]: e.target.value,
    });
  }

  useEffect(()=>{
    if(editableBook){
        setVideo(editableBook)
    }   
  },[editableBook]);

  return (
    <>
      <form>
        <input
          type="text"
          name="Name"
          onChange={handleChange}
          placeholder="Book"
          value={video.Name}
        />

        <input
          type="text"
          name="Author"
          onChange={handleChange}
          placeholder="Author"
          value={video.Author}
        />
        <input
          type="text"
          name="Publisher"
          onChange={handleChange}
          placeholder="Publication"
          value={video.Publisher}
        />
        <button onClick={handleSubmit}>{editableBook?'Edit Book':'Add'}Book</button>
      </form>
    </>
  );
}
export default AddVideo;
