import SideNavBar from "../Components/SideNavBar";
import NavBar from "../Components/NavBar";
import axios from 'axios';
import Box from "@mui/material/Box";
import { Button, Dialog, DialogTitle, DialogContent, Stack, DialogActions,TextField } from '@mui/material';
import { useState,useEffect } from "react";
import Bookdb from "../data/Data";
import BookList from "./BookList";
import AddVideo from "./AddVideo";
import Book from "./Book";
function Books() {
    const [open, setOpen] = useState(false);
    const[videos, setVideos] = useState(Bookdb);
    const [editableBook, setEditableBook] = useState(null)
    const [books, setBooks] = useState([]);
    const[Name,setName] = useState("");
    const[Author,setAuthor] = useState("");
    const[Publisher,setPublisher] = useState("");
    const[Availability,setAvailability] = useState("");
    const[Ledger,setLedger] = useState("");
    const[file, setfile] = useState("");

    
  const handleClickOpen = () => {
    setOpen(true);
  };

  const handleClose = () => {
    setOpen(false);
  };

  const handleClick = () => {
    const formData = new FormData();
    formData.append("Name",Name);
    formData.append("Author",Author);
    formData.append("Publisher",Publisher);
    formData.append("Ledger",Ledger);
    formData.append("Availability",Availability);
    formData.append("file",file);
    // axios.post('http://localhost:8000/book',formData)
    // .then((response)=>{console.log(response)})
    // .catch((error)=>{console.log(error)})

    axios.post("http://localhost:8000/book", formData)
    .then((response) => {
      console.log(response);
      fetchBooks(); 
      handleClose(); 
    })
    .catch((error) => {
      console.log(error);
    });
};

  
    
  function deleteBook(id){
    //yha delete ki api call hogi
    setVideos( videos.filter(video=>video.id !== id))
    fetch(`http://localhost:8000/book/${id}`,{
      method: 'DELETE'
    }).then((result)=>{
      result.json().then((response)=>{
          console.log(response)
      })
    })
   
     console.log(id)
  }

  function editBook(id){
    setEditableBook( videos.find(video=>video.id === id))
   
     console.log(id)
  }

  
  function updateBook(updatedBook) {
    const index = videos.findIndex(v => v.id === updatedBook.id);
    const newBooks = [...videos]; 
  
  
    newBooks.splice(index, 1, updatedBook);
  
  
    fetch(`http://localhost:8000/book/${updatedBook.id}`, {
      method: 'PUT',
      headers: {
        'Content-Type': 'application/json'
      },
      body: JSON.stringify(updatedBook) 
    })
    .then(result => {
      if (result.ok) {
        return result.json();
      }
      throw new Error('Network response was not ok.');
    })
    .then(response => {
      console.log(response);
      setVideos(newBooks); 
    })
    .catch(error => {
      console.error('There was a problem updating the book:', error);
      
    });
  }
  

  const fetchBooks = () => {
    axios.get('http://localhost:8000/book')
      .then((response) => {
        setVideos(response.data); 
      })
      .catch((error) => {
        console.error('Error fetching books:', error);
      });
  };

  useEffect(() => {
    fetchBooks();
  }, []);

  useEffect(() => {
    const params = new URLSearchParams(window.location.search);
    const authorName = params.get('author');

    // Fetch books related to the selected author
    fetch(`http://localhost:8000/booksByAuthor?author=${encodeURIComponent(authorName)}`)
      .then((response) => response.json())
      .then((data) => {
        setBooks(data.books); // Set the fetched books in state
      })
      .catch((error) => {
        console.error('Error fetching books:', error);
      });
  }, []);

    return(
        <>
            <NavBar />
      <Box height={30} />
      <Box sx={{ display: "flex" }}>
        <SideNavBar />

          <Box component="main" sx={{ flexGrow: 1, p: 3 }}>
          <h1>Books</h1>
          <Button color="primary" variant="contained" onClick={handleClickOpen} style={{ position: 'fixed', top: '90px',left: '90%'}} >AddBook</Button>
          <Dialog open={open} onClose={handleClose} fullWidth>
            <DialogTitle>UserScreen</DialogTitle>
            <DialogContent>
                
                <Stack spacing={2} margin={2}>
                 <TextField variant="outlined" label = "Book Name" onChange={(e)=>setName(e.target.value)}></TextField>
                 <TextField variant="outlined" label = "Author Name" onChange={(e)=>setAuthor(e.target.value)}></TextField>
                 <TextField variant="outlined" label = "Publisher Name"onChange={(e)=>setPublisher(e.target.value)}></TextField>
                 <TextField variant="outlined" label = "Availability"onChange={(e)=>setAvailability(e.target.value)}></TextField>
                 <TextField variant="outlined" label = "Ledger"onChange={(e)=>setLedger(e.target.value)}></TextField>
                 <TextField variant="outlined" label = "Book Image" type="file"onChange={(e)=>setfile(e.target.files[0])}></TextField>
                 <Button color="primary" variant="contained" onClick={handleClick}>AddBook</Button>
                </Stack>
            </DialogContent>
            <DialogActions>
               
                <Button color="error" variant="contained" onClick={handleClose}>Close</Button>
            </DialogActions>
          </Dialog>

          <div className="grid-container">
        {b.map(video=><Book 
                 key = {video.id}
                 id = {video.id}
                 Image = {video.Image}
                 Name = {video.Name}
                 Author = {video.Author}
                 Publisher = {video.Publisher}
                 Availability = {video.Availability}
                 Ledger = {video.Ledger}
                 deleteBook = {deleteBook}
                 editBook={editBook}
                 >

                 </Book>)}
                 </div>

          <AddVideo editableBook = {editableBook} updateBook={updateBook}></AddVideo>

          <BookList deleteBook={deleteBook} editBook={editBook} videos= {videos}></BookList>

          



        </Box>
      </Box>
        </>
    )
}
export default Books;
